import "../Components/RegisterForm.css"
import logo from '../Images/penguin-logo.png';
import { Button } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEye, faEyeSlash } from "@fortawesome/free-solid-svg-icons";
import userIcon from "../Images/userIcon.png";
import pwdIcon from "../Images/passwordIcon.png";

const RegisterForm = () => {
const handleClick = () => {
console.log("Clicked")
}

const wrongDetails = false


  return (
    <div className="wrapper">
      <div className="text-center pt-3">
        <img src={logo} className="penguin-logo" alt="penguin-logo"></img>
      </div>
      <div className="text-center mt4 name">Register</div>
      {/* {!fullPassword ? (
        <p className="text-center mt-4 wrongDetails">
          Username and/or password must be at least 6 characters
        </p>
      ) : (
        ""
      )}
      {wrongDetails ? (
        <p className="text-center mt-4 wrongDetails">
          Username is already taken. Please choose another.
        </p>
      ) : (
        ""
      )} */}
      <form className="p-3 mt-3 text-center">
        <div className="form-field d-flex align-items-center">

          <span>

            <img className="userIcon" alt="userIcon" src={userIcon} />
          </span>
          <input
            type="text"
            name="userName"
            id="userName"
            placeholder="Username"
            minlength="6"
            // onChange={(e) => setUsername(e.target.value)}
            // value={username}
          ></input>
        </div>
        <div className="form-field d-flex align-items-center input-group">
          <span>
            <img className="pwdIcon" alt="pwdIcon" src={pwdIcon} />
          </span>{" "}
          <input
            // type={passwordShown ? "text" : "password"}
            name="password"
            id="password"
            placeholder="Password"
            minlength="6"
            maxLength="15"
            // onChange={(e) => setPassword(e.target.value)}
            onKeyUp={(e) => {
              if (e.key === "Enter") {
                handleClick();
              }
            }}
            // value={password}
          ></input>
          <span className="passwordEyeIcon">
            <FontAwesomeIcon
              // icon={passwordShown ? faEye : faEyeSlash}
              // onClick={togglePasswordVisibility}
            />
          </span>
        </div>
        <Button className="btn mt-3 p-2 loginBtn" onClick={handleClick}>
          Register
        </Button>
      </form>

      <div className="text-center fs-6 p-3">
        {" "}
        Already have an account? <a href="/">Sign In</a>
      </div>
    </div>
  )
}

export default RegisterForm
